function Blogs() {


    const [data, setData] = useState([]);

    const query = `query{
        allPosts{
          id
          category
          title
          body
        }
      }`

    useEffect(() => {
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({query}),
        };

         fetch('http://127.0.0.1:8000/graphql', sendingPost)
            .then(response => response.json())
            .then(data => setData(data.data.allPosts));
    },[])
