import {React, useState, useEffect} from 'react'
import './Stats.css'

function Stats() {
    const [github,setGithub] = useState([])
    const [repos,setRepos] = useState([])

    useEffect(() => {
        fetch('https://api.github.com/users/iampranavdhar')
            .then(response => response.json())
            .then(result => setGithub(result));
        fetch('https://api.github.com/users/iampranavdhar/repos')
            .then(response => response.json())
            .then(repos => setRepos(repos))
    },[])

    const totalStars = () =>{
        const each_repo_stars = repos.map(({stargazers_count}) => stargazers_count) //For getting the list of all the stars for each repo
        const total = each_repo_stars.reduce((a, b) => a + b, 0) //For finding sum of the elements in a list
        return total
    }

    return (
        <div className='stats'>
            <h1 className='stats-title'>Stats</h1>
            <div className="github-followers">
                <p>GitHub Followers</p>
                <p>{github.followers}</p>
            </div>
            <div className="github-stars">
                <p>GitHub Stars</p>
                <p>{totalStars()}</p>
            </div>
            <div className="github-repos">
                <p>GitHub Repos</p>
            </div>
        </div>
    )
}

export default Stats
