import {React,useState,useEffect} from 'react'
import { useParams } from "react-router-dom";
import './BlogPage.css'
import Footer from './Footer';
import ReactMarkdown from "react-markdown";

function BlogPage() {

    const {id} = useParams();
    const [data,setData] = useState([])
    const query=`query{
        postById(id:${id}){
          id
          category
          coverImage
          title
          body
        }
      }
    `
    useEffect( ()=>{
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: JSON.stringify({query}),
        };

        fetch('http://127.0.0.1:8000/graphql', sendingPost)
        .then(response => response.json())
            .then(data => setData(data.data.postById));
    },[])

    console.log(data)

    const description = `${data.body}`

    return (
        <div className='blog__page'>
            <div className='blog__content'>
                <p className='blog__tile'>{data.title}</p>
                <img src='http://127.0.0.1:8000/media/django-summernote/2021-04-19.jpg'/>
                <p>{data.tags}</p>
                <p>{data.category}</p>
                {/* This dangerouslySetInnerHTML is for rendering exactly the html elements in the div without escaping the html tags */}
                <div
                    dangerouslySetInnerHTML={{
                    __html: description
                }}></div>
            </div>
        </div>
    )
}

export default BlogPage
