import './App.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import Nav from './Components/Nav';
import LandingPage from './Components/LandingPage';
import BlogPage from './Components/BlogPage';
import { ThemeProvider, createGlobalStyle } from 'styled-components';


const GlobalSyle = createGlobalStyle`
body{
  background-color: ${props =>
    props.theme.mode === 'dark' ? '#121212' : '#EEE'};
  color: ${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}
.icon{
  color:${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}

.icon:hover{
  color:${props => 
    props.theme.mode === 'dark' ? '#e04f62' : '#111'};
  }
.blog-card{
  background-color:${props => 
    props.theme.mode === 'dark' ? '#1F1F1F' : '#fff'};
  }
}
.article-details{
  color: ${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}
.post-title{
  color: ${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}
.header{
  background-color:${props => 
    props.theme.mode === 'dark' ? '#1d1d1d' : '#fff'};
  }
}
.logo-nav a{
  color: ${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}
}
`


function App() {
  <link href='https://cdn.jsdelivr.net/npm/boxicons@2.0.5/css/boxicons.min.css' rel='stylesheet'></link>
  return (
    <ThemeProvider theme={{mode:'dark'}}>
      <>
      <GlobalSyle/>
    <Router>
    <div className="App">
      <Nav/>
        <Switch>
          <Route exact path='/'>
            <LandingPage/>
          </Route>
          <Route exact path='/blogs/:id'>
            <BlogPage/>
          </Route>
        </Switch>
    </div>
    </Router>
    </>
    </ThemeProvider>
  );
}

export default App;
