import {React, useState} from 'react'
import './Nav.css'

import MenuIcon from '@material-ui/icons/Menu';
import ClearIcon from '@material-ui/icons/Clear';
import HomeIcon from '@material-ui/icons/Home';
import WbSunnyIcon from '@material-ui/icons/WbSunny';
import NightsStayIcon from '@material-ui/icons/NightsStay';


function Nav({themeSetter,theme}) {

    const [toggle, settoggle] = useState(false)

    // For getting Close Menu Optn when clicked on Menu Options and
    // for Menu Bar when it is active for getting out when clicked on it
    const Toggle = () =>{
        settoggle(!toggle)
        console.log(toggle)
    }

    // For closing the Sidebar when link clicked
    const closeMenu = () => {
        settoggle(false)
    }

    return (
        <div className="header">
            <div className="logo-nav">
                <div>
                    <a className='left-logo' href="#home" onClick={()=>{closeMenu()}}>Pranavdhar</a>
                </div>
                <div className='right-navigations'>
                    <div className={toggle? "nav-options active" : "nav-options"}>
                        <a onClick={()=>{closeMenu()}} href="#home">Home</a>
                        <a onClick={()=>{closeMenu()}} href="#about">About</a>
                        <a onClick={()=>{closeMenu()}} href="#home">Skills</a>
                        <a onClick={()=>{closeMenu()}} href="#projects">Projects</a>
                        <a onClick={()=>{closeMenu()}} href="#blogs">Blogs</a>
                    </div>
                    <div className='theme-toggle'>
                        <WbSunnyIcon style={{ fontSize: 40}}/>
                    </div>
                </div>

            </div>
            <div className="mobile-nav">
                <div className='mobile-menu'>
                    {
                        theme === "light" 
                        ? 
                        <NightsStayIcon onClick={themeSetter} className='theme-toggle' style={{ fontSize: 40}}/>
                        :
                        <WbSunnyIcon onClick={themeSetter} className='theme-toggle' style={{ fontSize: 40}}/>


                    }

                    <div onClick={() =>{Toggle()}}>
                        {toggle ? (
                            <ClearIcon className="menu-icon" style={{ fontSize: 40}}/>
                            ) : (
                            <MenuIcon className="menu-icon" style={{ fontSize: 40}}/>
                        )}
                    </div>
                </div>

            </div>
        </div>
    )
}

export default Nav
