import {React,useState,useEffect} from 'react'
import { Link } from 'react-router-dom';
import BlogCard from './BlogCard'
import './Blogs.css'

function Blogs() {
    const [data, setData] = useState([]);
    const query = `query{
        allPosts{
          id
          category
          title
          coverImage
          card
          body
        }
      }`

    useEffect(() => {
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({query}),
        };

         fetch('http://127.0.0.1:8000/graphql', sendingPost)
            .then(response => response.json())
            .then(data => setData(data.data.allPosts));
    },[])
    console.log(data);

    return (
        <div id='blogs'>
        <p className='blogs-title'>Blogs</p>
        <div className='blogs__container'>
            {data.map((blog) =>
            <Link to={`blogs/${blog.id}`} style={{textDecoration:'none',color:'black'}}>
                <BlogCard
                    coverimage={"http://127.0.0.1:8000/media/"+`${blog.coverImage}`}
                    category={blog.category}
                    title={blog.title}
                    descriptionpart={blog.body.substring(0,105)}
                />
            </Link>
            )}
        </div>
        </div>
    )
}

export default Blogs
