import {React,useState,useEffect} from 'react'
import { useParams } from "react-router-dom";
import './BlogPage.css'
import LinkedInIcon from '@material-ui/icons/LinkedIn';
import TwitterIcon from '@material-ui/icons/Twitter';
import GitHubIcon from '@material-ui/icons/GitHub';
import TelegramIcon from '@material-ui/icons/Telegram';

function BlogPage() {

    const {id} = useParams();
    const [data,setData] = useState([])
    const query=`query{
        postById(id:${id}){
          id
          title
          body
          category
        }
      }
    `
    useEffect( ()=>{
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: JSON.stringify({query}),
        };

        fetch('http://127.0.0.1:8000/graphql', sendingPost)
        .then(response => response.json())
            .then(data => setData(data.data.postById));
    },[])

    console.log(data)

    return (
        <div >
            <div className='blog__page'>
                <p className='blog__content'>{data.title}</p>
                <p className='blog__content'>{data.tags}</p>
                <p className='blog__content'>{data.category}</p>
                <p className='blog__content'>{data.body}</p>
            </div>
            <div className='footer'>
            <p className="footer__title">Pranavdhar</p>
            <div className="footer__social">
                <a href="https://www.linkedin.com/in/sai-pranavdhar-reddy-nalamalapu-038104206/" className="footer__icon"><LinkedInIcon/></a>
                <a href="#home" className="footer__icon"><TelegramIcon/></a>
                <a href="https://twitter.com/Pranavdhar8" className="footer__icon"><TwitterIcon/></a>
                <a href="https://github.com/iampranavdhar" className="footer__icon"><GitHubIcon/></a>
            </div>
            <p className='footer__copyright'>&#169; 2020 copyright all right reserved</p>
            </div>
        </div>
    )
}

export default BlogPage
