import {React, useState} from 'react'
import './Nav.css'
import MenuIcon from '@material-ui/icons/Menu';
import ClearIcon from '@material-ui/icons/Clear';


function Nav() {

    const [toggle, settoggle] = useState(false)

    const Toggle = () =>{
        settoggle(!toggle)
        console.log(toggle)
    }

    const closeMenu = () => {
        settoggle(false)
    }

    return (
            <div className='nav'>
                <div class>
                <div>
                    <a href="#" className="nav__logo">Pranavdhar</a>
                </div>

                <div className="navBar">
                    <ul className={toggle ? "nav-links show-nav" : "nav-links"}>
                        <li onClick={()=>{closeMenu()}} class="nav__item"><a href="#home" class="nav__link active">Home</a></li>
                        <li onClick={()=>{closeMenu()}} class="nav__item"><a href="#about" class="nav__link">About</a></li>
                        <li onClick={()=>{closeMenu()}} class="nav__item"><a href="#skills" class="nav__link">Skills</a></li>
                        <li onClick={()=>{closeMenu()}} class="nav__item"><a href="#work" class="nav__link">Projects</a></li>
                        <li onClick={()=>{closeMenu()}} class="nav__item"><a href="#contact" class="nav__link">Contact</a></li>
                    </ul>
                </div>
                </div>

                <div className='button' onClick={ () =>{Toggle()}}>
                    {/* Making it the () => {Toggle()} solved 
                    the error "Error: Too many re-renders. React 
                    limits the number of renders to prevent an 
                    infinite loop." */}
                    {toggle? (
                        <ClearIcon/>
                    ):(
                    <MenuIcon />
                    )}
                </div>
            </div>
    )
}

export default Nav
