import React from 'react'
import './Contact.css'
import emailjs from 'emailjs-com';
import Config from './Config.js';

function Contact() {


    const {data} = Config();
    console.log(data.SECRET_API_KEY)

    const sendEmail = (e) => {

        e.preventDefault();
        emailjs.sendForm(data.SERVICE_ID, data.TEMPLATE_ID, e.target, data.SECRET_API_KEY)
            .then((result) => {
                console.log(result.text);
                alert("Your Message has been delivered");
            }, (error) => {
                console.log(error.text);
            });
            e.target.reset();   
    }

    return (
        <div className='contact' id='contact'>
            <h2 className='contact-title'>Contact</h2>
            <div className="contact-container">
                <img className='contact-image' src='./assets/contact.png' alt=''></img>
                <form onSubmit={sendEmail}>
                    <label for="name">Name</label>
                    <input type="text" id="name" name="name" placeholder="Your name.." required/>

                    <label for="email">Email</label>
                    <input type="text" id="email" name="email" placeholder="Your email..." required/>

                    <label for="message">Message</label>
                    <textarea id="message" name="message" placeholder="Write something.." required></textarea>

                    <input type="submit" value="Submit" />
                </form>
            </div>
        </div>
    )
}

export default Contact
