import {useState, useEffect} from 'react'

const useFetch = () => {

    const {id} = useParams();

    const [data,setData] = useState([])
    const data_query=`query{
        postById(id:${id}){
          id
          category
          coverImage
          title
          body
        }
      }
    `
    useEffect( ()=>{
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: JSON.stringify({data_query}),
        };

        fetch('http://127.0.0.1:8000/graphql', sendingPost)
        .then(response => response.json())
            .then(data => setData(data.data.postById));
    },[id])

    return [data];
}

export default useFetch
