import {useState} from 'react'
import './App.css';
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { ThemeProvider, createGlobalStyle } from 'styled-components';
import {useTheme} from './Components/useTheme';

import Nav from './Components/Nav';
import LandingPage from './Components/LandingPage';
import BlogPage from './Components/BlogPage';


//These are the styles that need to be changed when we move to dark theme.
const GlobalSyle = createGlobalStyle`
body{
  background-color: ${props =>
    props.theme.mode === 'dark' ? '#121212' : '#EEE'};
  color: ${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}
.icon{
  color:${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}

.icon:hover{
  color:${props => 
    props.theme.mode === 'dark' ? '#e04f62' : '#e04f62'};
  }
.blog-card{
  background-color:${props => 
    props.theme.mode === 'dark' ? '#1F1F1F' : '#fff'};
  }
}
.article-details,.post-title{
  color: ${props => 
    props.theme.mode === 'dark' ? '#d9dedc' : '#111'};
  }
}
.header{
  background-color:${props => 
    props.theme.mode === 'dark' ? '#1F1F1F' : '#fff'};
  }
}
.logo-nav a,.projects p,.home-title,.blogs-title,.projects-title{
  color: ${props => 
    props.theme.mode === 'dark' ? '#EEE' : '#111'};
  }
}
}
`

function App() {

  const [theme,setTheme] = useState("light")

  const themeToggler =() =>{
    theme = "light" ? setTheme("dark") : setTheme("light");
  }

  const name = "light";
  return (
    <ThemeProvider theme={{mode:`${name}`}}>
      <>
      <GlobalSyle/>
    <Router>
    <div className="App">
      <Nav themeSetter={themeToggler()}/>
        <Switch>
          <Route exact path='/'>
            <LandingPage/>
          </Route>
          <Route exact path='/blogs/:id'>
            <BlogPage/>
          </Route>
        </Switch>
    </div>
    </Router>
    </>
    </ThemeProvider>
  );

}

export default App;
