import {React,useState,useEffect} from 'react'
import { useParams } from "react-router-dom";
import ReactMarkdown from 'react-markdown';
import { Prism as SyntaxHighlighter } from 'react-syntax-highlighter';
import { dark } from 'react-syntax-highlighter/dist/esm/styles/prism';

import './BlogPage.css';

function BlogPage() {

    const {id} = useParams();
    const [data,setData] = useState([])
    const query=`query{
        postById(id:${id}){
          id
          category
          coverImage
          title
          body
        }
      }
    `
    useEffect( ()=>{
        const sendingPost = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },

            body: JSON.stringify({query}),
        };

        fetch('http://127.0.0.1:8000/graphql', sendingPost)
        .then(response => response.json())
            .then(data => setData(data.data.postById));
    },[])

    const description = `${data.body}`;
    const coverimage = "http://127.0.0.1:8000/media/"+data.coverImage;


    const Component = ({description,language}) => {
        return (
          <SyntaxHighlighter language={language ?? null} style={dark}>
            {description ?? ''}
          </SyntaxHighlighter>
        );
      };


    return (
        <div className='blog__page'>
            <div className='blog__content'>
                <div>
                    <p className='blog__title'>{data.title}</p>
                </div>
                <div>
                    <p>{data.tags}</p>
                    <p>{data.category}</p>
                </div>
                <div>
                    <img className = 'blog__coverimage' src={coverimage} alt='CoverImage'/>
                </div>

                {/* This dangerouslySetInnerHTML is for rendering exactly the html elements in the div without escaping the html tags */}
                {/* <div className='blog__description'
                    dangerouslySetInnerHTML={{
                    __html: description
                }}></div> */}
                <ReactMarkdown children={description} components={{
                    code:Component,
                }}></ReactMarkdown>
            </div>
        </div>
    )
}

export default BlogPage
