import {useState} from 'react'

const useTheme = () => {
    const [theme, setTheme] = useState('light');

    const themetoggle = () =>{
        theme = "light" ? setTheme("dark") : setTheme("light");
    }
    return [theme, themetoggle]
}

export default useTheme;
